func randomAnswerState() -> AnswerState {
    let states: [AnswerState] = [.isCorrect, .isWrong, .isPassed,]
    return states.randomElement() ?? .isCorrect
}

func randomUserAnswer() -> String {
    let letters = ["aaaaaaaa", "bbbbbbb", "ccccccc", "ddddddd", "eeeeee", "-"]
    return letters.randomElement() ?? "-"
}

let fakeDataWithAnswer = fakeData.map({
    Question(letter: $0.letter, meaning: $0.meaning, word: $0.word, answerState: randomAnswerState(),userAnswer: randomUserAnswer())
})
